<script>
export default {
  name: 'tt-icon',
  props: {
    icon: { type: String, required: true, default: '' },
    ttstyle: { type: Object, required: false, default: () => ({}) },
    color: { type: String, required: false, default: 'primary' },
    size: { type: String, required: false, default: '1em' },
  },
  computed: {
    _size() {
      const split = this.size.split(/[\s]+/g)
      return {
        width: split[0],
        height: split.length > 1 ? split[1] : split[0]
      }
    },
    _style() {
      let color = '#E5831B'
      if (this.color === 'bg') color = '#1E232B'
      if (this.color === 'secondary') color = '#FAE6D1'
      if (this.color === 'inherit') color = 'inherit'
      if (this.color.startsWith('#')) color = this.color
      return {
        ...this.style,
        color,
        fill: color
      }
    }
  }
}
</script>

<style scoped>
.tt-lib-icon-container {
  line-height: 0;
  transition-duration: .2s;
  transition-property: fill;
  transition-timing-function: ease;
}
.tt-lib-icon-container * {
  color: inherit;
  fill: inherit;
  border: none;
  stroke: none;
}
</style>